import{_ as e,o as r,c as o,ae as t}from"./chunks/framework.tEEY0ABY.js";const i="/MM_TotalKeywordTime_wiki/images/%E3%82%BF%E3%82%B0%E3%81%AE%E8%89%B2%E3%82%92%E5%A4%89%E6%9B%B4.png",l="/MM_TotalKeywordTime_wiki/images/%E3%82%BF%E3%82%B0%E3%81%AE%E8%89%B2%E3%82%92%E5%A4%89%E6%9B%B4_UI.png",p=JSON.parse('{"title":"タグ付き予定の色を一括変更","description":"","frontmatter":{},"headers":[],"relativePath":"guide/features/change-color.md","filePath":"guide/features/change-color.md"}'),n={name:"guide/features/change-color.md"};function s(g,a,c,d,h,u){return r(),o("div",null,[...a[0]||(a[0]=[t('<h1 id="タグ付き予定の色を一括変更" tabindex="-1">タグ付き予定の色を一括変更 <a class="header-anchor" href="#タグ付き予定の色を一括変更" aria-label="Permalink to &quot;タグ付き予定の色を一括変更&quot;">​</a></h1><p>特定のタグが含まれる予定の色を、指定したルールに基づいて一括で変更・統一することができます。<br> 例えば、「#会議」タグがついた予定をすべて「赤色（トマト）」に変更するといった操作が可能です。</p><p><img src="'+i+'" alt="タグの色を変更"></p><h2 id="概要" tabindex="-1">概要 <a class="header-anchor" href="#概要" aria-label="Permalink to &quot;概要&quot;">​</a></h2><p>Googleカレンダーでは予定の色を個別に設定できますが、この機能を使うことで、タグごとのルールとして色を管理・適用できます。</p><ul><li><strong>一括適用</strong>: 過去および未来の予定（自身が作成したもの）に対して、設定した色を一括で反映します。</li><li><strong>対象の絞り込み</strong>: 「除外タグを除いて表示」などのフィルタリングが可能です。</li></ul><h2 id="操作方法" tabindex="-1">操作方法 <a class="header-anchor" href="#操作方法" aria-label="Permalink to &quot;操作方法&quot;">​</a></h2><p>各タグの横にあるカラーパレットから、適用したい色を選択します。</p><p><img src="'+l+'" alt="タグの色を変更_UI"></p><h3 id="手順" tabindex="-1">手順 <a class="header-anchor" href="#手順" aria-label="Permalink to &quot;手順&quot;">​</a></h3><ol><li><strong>色の選択</strong>: リストから対象のタグ（例: <code>#meeting</code>）を探し、適用したい色のボタンをクリックします。</li><li><strong>色の解除</strong>: 色設定を解除したい場合は、「未設定」ボタンをクリックします。</li><li><strong>反映の実行</strong>: 設定が完了したら、右下の <strong>「適用」ボタン</strong> をクリックします。 <ul><li>進捗バーが表示され、対象となる予定の色が順次更新されます。</li></ul></li></ol><h3 id="オプション" tabindex="-1">オプション <a class="header-anchor" href="#オプション" aria-label="Permalink to &quot;オプション&quot;">​</a></h3><ul><li><strong>終日予定のみ対象</strong>: チェックを入れると、時間の指定がない「終日予定」のみ色変更の対象とします。</li><li><strong>除外タグを除いて表示</strong>: 「入力候補タグの指定」などで除外設定にしたタグをリストから隠します。</li></ul><h2 id="仕様" tabindex="-1">仕様 <a class="header-anchor" href="#仕様" aria-label="Permalink to &quot;仕様&quot;">​</a></h2><ul><li><strong>色の変更対象</strong>: 色の変更が適用されるのは、<strong>ログイン中のユーザー自身が作成した予定（Organizerが自分である予定）</strong> のみです。</li><li><strong>共有予定の制限</strong>: 他のユーザーから招待された予定や、書き込み権限がない共有カレンダーの予定は、色の変更対象外となります。</li></ul>',15)])])}const m=e(n,[["render",s]]);export{p as __pageData,m as default};
